const searchInput=document.getElementById("search"),searchBtn=document.getElementById("searchBtn"),pages=document.getElementById("pages"),userName=document.querySelectorAll(".userName"),students=document.querySelectorAll(".student");function search(){const e=searchInput.value,t=new RegExp(e,"i");userName.forEach(a=>{const s=t.test(a.innerHTML);s?s&&(a.parentNode.parentNode.parentNode.parentNode.classList.remove("hide"),""===e&&addHideClass("page1")):a.parentNode.parentNode.parentNode.parentNode.classList.add("hide")})}function addHideClass(e){students.forEach(t=>{t.classList.contains(e)?t.classList.remove("hide"):t.classList.add("hide")})}pages.addEventListener("click",e=>{"BUTTON"===e.target.tagName&&("1"===e.target.textContent?addHideClass("page1"):"2"===e.target.textContent?addHideClass("page2"):"3"===e.target.textContent?addHideClass("page3"):"4"===e.target.textContent&&addHideClass("page4"))}),searchBtn.addEventListener("click",search,!1),searchInput.addEventListener("focus",()=>{searchInput.removeAttribute("placeholder")}),searchInput.addEventListener("blur",()=>{searchInput.setAttribute("placeholder","Search for Students..")});